#include "EquipmentLayer.h"cocos2d::Layer* EquipmentLayer::createLayer(){	return EquipmentLayer::create();}static void problemLoading(const char* filename){	printf("Error while loading: %s\n", filename);	printf("Depending on how you compiled you might have to add 'Resources/' in front of filenames in StartSceneScene.cpp\n");}bool EquipmentLayer::init(){	if (!Layer::init())	{		return false;	}	auto visibleSize = Director::getInstance()->getVisibleSize();	auto origin = Director::getInstance()->getVisibleOrigin();	auto swordone = MenuItemImage::create(		"equipment/swordone,png",		"equipment/swordone,png",		CC_CALLBACK_1(EquipmentLayer::menuSwordOneCallBack, this)	);	if (swordone == nullptr ||		swordone->getContentSize().width <= 0 ||		swordone->getContentSize().height <= 0)	{		problemLoading("'equipment/swordone.png' and 'equipment/swordone.png'");	}	else	{		swordone->setPosition(Vec2(origin.x + visibleSize.width * 3 / 4, origin.y + visibleSize.height / 2));	}	//添加其他物品图标	auto menu = Menu::create(swordone, NULL);	menu->setPosition(Vec2::ZERO);	this->addChild(menu, 2);	return true;}void EquipmentLayer::menuSwordOneCallBack(cocos2d::Ref * pSender){	Director::getInstance()->end();}